  const { items, del } = props;
  let length = items.length;

  const ProductQuantity = () => {
    const [quantity, setQuantity] = useState(0);

    const increaseQuantity = () => {
      setQuantity(quantity + 1);
    };

    const decreaseQuantity = () => {
      if (quantity > 1) {
        setQuantity(quantity - 1);
      }
    };
